from locust import HttpUser, task, between


class WebsiteTestUser(HttpUser):

    wait_time = between(0.5, 3.0)
    
    def on_start(self):
        print("Starting Load Test")

    def on_stop(self):
        print("Stopping Load Test")

    @task(1)
    def signup(self):
        payload = "{ \n    \"username\": \"random\",\n    \"image64\": \"data:image/jpeg;base64,\"\n}"
        headers = {
            'Content-Type': "application/json",
            'Cache-Control': "no-cache",
            'Postman-Token': "e9429159-622a-962b-a546-8bcf7e71dc1b"
        }
        self.client.post("/register", data=payload, headers=headers)
